ALTER TABLE transactions 
ADD COLUMN IF NOT EXISTS approvedBy INT AFTER reference,
ADD COLUMN IF NOT EXISTS approvedAt TIMESTAMP NULL AFTER approvedBy,
ADD COLUMN IF NOT EXISTS rejectionReason TEXT NULL AFTER approvedAt;

ALTER TABLE transactions 
MODIFY COLUMN status ENUM('pending', 'pending_approval', 'completed', 'failed', 'cancelled') NOT NULL DEFAULT 'pending';

